import React from "react"

class DivWrapper extends React.Component {

    constructor(props) {
        super(props)
    }

    render() {
        return null
    }

    componentDidMount() {
        this.props.onHeightChange(React.findDOMNode(this).offsetHeight)
    }

    componentDidUpdate() {
        this.props.onHeightChange(React.findDOMNode(this).offsetHeight)
    }

    coponentWillUnmount() {
        this.props.onHeightChange(0)
    }

}